mapper.update:
UPDATE members SET 
	address = #{param1}, detailaddress = #{param2}, acaname = #{param3}, telephone1=#{param4}, telephone2 = #{param5}, telephone3 =#{param6}
	WHERE id = #{param7}

UPDATE acaclass SET 
		castartdate=#castartdate}, acaenddate=#{acaenddate}, acaday=#{acaday}, 
		acastarttime=#{acastarttime},acaendtime=#{acaendtime}, acaclassname=#{acaclassname}, 
		pay=#{pay}, numberofparticipants=#{numberofparticipants}
WHERE classid=#{classid}
